'use strict';

const src = '';
const fs = require('fs');

module.exports = function(src, dest){

  var obj = {};
  var index = 0;

  fs.readdirSync(src).filter(function(file) {
    if(file.indexOf(".html")>-1) {
      obj[index] = file;

      fs.writeFile(dest, JSON.stringify(obj), function (err) {
        if (err) throw err;
        console.log('File write completed');
      });

      index++;
    }
  });

};
